webpackJsonp([42172547050889],{3457:function(t,e){t.exports={data:{challengeNode:{title:"Add a Hover Effect to a D3 Element",description:["当用户用鼠标悬停在组上时，可以添加高亮显示栏的效果。到目前为止，矩形的样式应用了内置的 D3 和 SVG 方法，但是你也可以使用 CSS。","你可是使用<code>attr()</code>方法在 SVG 元素上设置 CSS 类。然后用<code>:hover</code>伪类为你新添加的 CSS 类设置鼠标悬停的效果。","<hr>","用<code>attr()</code>方法给所有的<code>rect</code>元素都添加<code>bar</code>类。当鼠标悬浮在一组上时，这将使它的<code>fill</code>颜色变为 brown。"],challengeType:0,fields:{slug:"/data-visualization/data-visualization-with-d3/add-a-hover-effect-to-a-d3-element",blockName:"Data Visualization with D3",tests:[{text:"<code>rect</code>元素应该有<code>bar</code>类。",testString:"assert($('rect').attr('class') == \"bar\", '<code>rect</code>元素应该有<code>bar</code>类。');"}]},required:[{link:null,raw:null,src:"https://cdnjs.cloudflare.com/ajax/libs/d3/4.3.0/d3.min.js"}],files:{indexhtml:{key:"indexhtml",ext:"html",name:"index",contents:'<style>\n  .bar:hover {\n    fill: brown;\n  }\n</style>\n<body>\n  <script>\n    const dataset = [12, 31, 22, 17, 25, 18, 29, 14, 9];\n    \n    const w = 500;\n    const h = 100;\n    \n    const svg = d3.select("body")\n                  .append("svg")\n                  .attr("width", w)\n                  .attr("height", h);\n    \n    svg.selectAll("rect")\n       .data(dataset)\n       .enter()\n       .append("rect")\n       .attr("x", (d, i) => i * 30)\n       .attr("y", (d, i) => h - 3 * d)\n       .attr("width", 25)\n       .attr("height", (d, i) => 3 * d)\n       .attr("fill", "navy")\n       // 在下面添加你的代码\n       \n       \n       \n       // 在上面添加你的代码\n    \n    svg.selectAll("text")\n       .data(dataset)\n       .enter()\n       .append("text")\n       .text((d) => d)\n       .attr("x", (d, i) => i * 30)\n       .attr("y", (d, i) => h - (3 * d) - 3);\n       \n  </script>\n</body>',head:"",tail:""},indexjs:null,indexjsx:null}}},pathContext:{challengeMeta:{introPath:"",template:null,required:[{link:null,raw:null,src:"https://cdnjs.cloudflare.com/ajax/libs/d3/4.3.0/d3.min.js"}],nextChallengePath:"/data-visualization/data-visualization-with-d3/add-a-tooltip-to-a-d3-element",id:"587d7faa367417b2b2512bd4"},slug:"/data-visualization/data-visualization-with-d3/add-a-hover-effect-to-a-d3-element"}}}});
