webpackJsonp([0xaa2699931b45],{3458:function(t,e){t.exports={data:{challengeNode:{title:"Add a Tooltip to a D3 Element",description:["当用户在一个对象上悬停时，提示框可以显示关于这个对象更多的信息。在可视化中有多种方法添加提示框，这个挑战将使用 SVG 的<code>title</code>元素。","<code>tile</code>和<code>text()</code>方法一起给每组动态添加数据。","<hr>","在每个<code>rect</code>节点下面添加一个<code>title</code>元素，然后用回调函数调用<code>text()</code>方法使它的文本显示数据值。"],challengeType:0,fields:{slug:"/data-visualization/data-visualization-with-d3/add-a-tooltip-to-a-d3-element",blockName:"Data Visualization with D3",tests:[{text:"你应该有 9 个<code>title</code>元素。",testString:"assert($('title').length == 9, '你应该有 9 个<code>title</code>元素。');"},{text:"第一个<code>title</code>元素的提示框文本应为 12。",testString:"assert($('title').eq(0).text() == '12', '第一个<code>title</code>元素的提示框文本应为 12。');"},{text:"第二个<code>title</code>元素的提示框文本应为 31。",testString:"assert($('title').eq(1).text() == '31', '第二个<code>title</code>元素的提示框文本应为 31。');"},{text:"第三个<code>title</code>元素的提示框文本应为 22。",testString:"assert($('title').eq(2).text() == '22', '第三个<code>title</code>元素的提示框文本应为 22。');"},{text:"第四个<code>title</code>元素的提示框文本应为 17。",testString:"assert($('title').eq(3).text() == '17', '第四个<code>title</code>元素的提示框文本应为 17。');"},{text:"第五个<code>title</code>元素的提示框文本应为 25。",testString:"assert($('title').eq(4).text() == '25', '第五个<code>title</code>元素的提示框文本应为 25。');"},{text:"第六个<code>title</code>元素的提示框文本应为 18。",testString:"assert($('title').eq(5).text() == '18', '第六个<code>title</code>元素的提示框文本应为 18。');"},{text:"第七个<code>title</code>元素的提示框文本应为 29。",testString:"assert($('title').eq(6).text() == '29', '第七个<code>title</code>元素的提示框文本应为 29。');"},{text:"第八个<code>title</code>元素的提示框文本应为 14。",testString:"assert($('title').eq(7).text() == '14', '第八个<code>title</code>元素的提示框文本应为 14。');"},{text:"第九个<code>title</code>元素的提示框文本应为 9。",testString:"assert($('title').eq(8).text() == '9', '第九个<code>title</code>元素的提示框文本应为 9。');"}]},required:[{link:null,raw:null,src:"https://cdnjs.cloudflare.com/ajax/libs/d3/4.3.0/d3.min.js"}],files:{indexhtml:{key:"indexhtml",ext:"html",name:"index",contents:'<style>\n  .bar:hover {\n    fill: brown;\n  }\n</style>\n<body>\n  <script>\n    const dataset = [12, 31, 22, 17, 25, 18, 29, 14, 9];\n    \n    const w = 500;\n    const h = 100;\n    \n    const svg = d3.select("body")\n                  .append("svg")\n                  .attr("width", w)\n                  .attr("height", h);\n    \n    svg.selectAll("rect")\n       .data(dataset)\n       .enter()\n       .append("rect")\n       .attr("x", (d, i) => i * 30)\n       .attr("y", (d, i) => h - 3 * d)\n       .attr("width", 25)\n       .attr("height", (d, i) => d * 3)\n       .attr("fill", "navy")\n       .attr("class", "bar")\n       // 在下面添加你的代码\n       \n       \n       \n       // 在上面添加你的代码\n    \n    svg.selectAll("text")\n       .data(dataset)\n       .enter()\n       .append("text")\n       .text((d) => d)\n       .attr("x", (d, i) => i * 30)\n       .attr("y", (d, i) => h - (d * 3 + 3))   \n    \n  </script>\n</body>',head:"",tail:""},indexjs:null,indexjsx:null}}},pathContext:{challengeMeta:{introPath:"",template:null,required:[{link:null,raw:null,src:"https://cdnjs.cloudflare.com/ajax/libs/d3/4.3.0/d3.min.js"}],nextChallengePath:"/data-visualization/data-visualization-with-d3/create-a-scatterplot-with-svg-circles",id:"587d7faa367417b2b2512bd6"},slug:"/data-visualization/data-visualization-with-d3/add-a-tooltip-to-a-d3-element"}}}});
