webpackJsonp([0xd78b164cde1e],{3479:function(t,e){t.exports={data:{challengeNode:{title:"Style D3 Labels",description:['D3 可以将样式添加到组标签中。<code>fill</code>属性为<code>text</code>节点设置文本颜色，<code>style()</code>方法设置其它样式的 CSS 规则，例如 "font-family"、"font-size"。',"<hr>","将<code>text</code>元素的<code>font-size</code>设置为 25px，文本颜色设置为 red。"],challengeType:0,fields:{slug:"/data-visualization/data-visualization-with-d3/style-d3-labels",blockName:"Data Visualization with D3",tests:[{text:"所有标签的<code>fill</code>颜色应该是 red。",testString:"assert($('text').css('fill') == 'rgb(255, 0, 0)', '所有标签的<code>fill</code>颜色应该是 red。');"},{text:"所有标签的<code>font-size</code>应该为 25 个像素。",testString:"assert($('text').css('font-size') == '25px', '所有标签的<code>font-size</code>应该为 25 个像素。');"}]},required:[{link:null,raw:null,src:"https://cdnjs.cloudflare.com/ajax/libs/d3/4.3.0/d3.min.js"}],files:{indexhtml:{key:"indexhtml",ext:"html",name:"index",contents:'<body>\n  <script>\n    const dataset = [12, 31, 22, 17, 25, 18, 29, 14, 9];\n    \n    const w = 500;\n    const h = 100;\n    \n    const svg = d3.select("body")\n                  .append("svg")\n                  .attr("width", w)\n                  .attr("height", h);\n    \n    svg.selectAll("rect")\n       .data(dataset)\n       .enter()\n       .append("rect")\n       .attr("x", (d, i) => i * 30)\n       .attr("y", (d, i) => h - 3 * d)\n       .attr("width", 25)\n       .attr("height", (d, i) => d * 3)\n       .attr("fill", "navy");\n    \n    svg.selectAll("text")\n       .data(dataset)\n       .enter()\n       .append("text")\n       .text((d) => d)\n       .attr("x", (d, i) => i * 30)\n       .attr("y", (d, i) => h - (3 * d) - 3)\n       // 在下面添加你的代码\n       \n       \n       \n       // 在上面添加你的代码\n  </script>\n</body>',head:"",tail:""},indexjs:null,indexjsx:null}}},pathContext:{challengeMeta:{introPath:"",template:null,required:[{link:null,raw:null,src:"https://cdnjs.cloudflare.com/ajax/libs/d3/4.3.0/d3.min.js"}],nextChallengePath:"/data-visualization/data-visualization-with-d3/add-a-hover-effect-to-a-d3-element",id:"587d7faa367417b2b2512bd3"},slug:"/data-visualization/data-visualization-with-d3/style-d3-labels"}}}});
